import React, { useState } from 'react';

const CyberInsuranceCard: React.FC = () => {
  const [isExpanded, setIsExpanded] = useState(false);

  return (
    <div className="relative cyber-card-container">
      {/* Base Cyber Insurance Card */}
      <div 
        className={`relative w-[300px] h-[170px] bg-light-gray rounded-[34px] p-6 cyber-card-base cursor-pointer ${
          isExpanded ? 'z-20' : 'z-10'
        }`}
        onMouseEnter={() => setIsExpanded(true)}
        onMouseLeave={() => setIsExpanded(false)}
      >
        {/* Card Content */}
        <div className="flex justify-between items-start h-full">
          <div className="space-y-3">
            {/* Title */}
            <h3 className="font-satoshi font-normal text-[30px] text-design-black leading-[1.16em]">
              Cyber
            </h3>
            {/* Description */}
            <p className="font-satoshi font-light text-[17px] text-design-black leading-[1.16em] max-w-[150px]">
              Protect your<br />digital life
            </p>
          </div>
          {/* Icon */}
          <img 
            src="/images/cyber-security-icon.png" 
            alt="Cyber Security Icon" 
            className="w-[48px] h-[48px] object-contain"
          />
        </div>
      </div>

      {/* Expanded Content - Overlay - Expanding Upward */}
      <div 
        className={`absolute bottom-0 left-0 w-[300px] bg-light-gray rounded-[34px] cyber-card-expanded overflow-hidden shadow-xl ${
          isExpanded ? 'h-[364px] opacity-100 z-30' : 'h-[170px] opacity-0 pointer-events-none'
        }`}
        onMouseEnter={() => setIsExpanded(true)}
        onMouseLeave={() => setIsExpanded(false)}
      >
        {/* Expanded Content Container */}
        <div className="p-6 h-full flex flex-col">
          {/* Header Section - Only Title and Icon */}
          <div className="flex justify-between items-start mb-6">
            <div className="space-y-3">
              <h3 className="font-satoshi font-normal text-[30px] text-design-black leading-[1.16em]">
                Cyber
              </h3>
              {/* Description removed from expanded view */}
            </div>
            <img 
              src="/images/cyber-security-icon.png" 
              alt="Cyber Security Icon" 
              className="w-[48px] h-[48px] object-contain"
            />
          </div>

          {/* Cyber Security Statistics Content */}
          <div className="flex-1 space-y-4">
            {/* Bullet Points */}
            <div className="space-y-3">
              {/* First Bullet Point */}
              <div className="flex items-start space-x-2">
                <span className="w-1.5 h-1.5 bg-design-black rounded-full mt-2 flex-shrink-0"></span>
                <p className="font-satoshi font-light text-[15px] text-design-black leading-[1.4em]">
                  SMBs are now the #1 target for hackers fewer resources, bigger pay-outs.
                </p>
              </div>

              {/* Second Bullet Point */}
              <div className="flex items-start space-x-2">
                <span className="w-1.5 h-1.5 bg-design-black rounded-full mt-2 flex-shrink-0"></span>
                <p className="font-satoshi font-light text-[15px] text-design-black leading-[1.4em]">
                  India was the #2 most-attacked nation in 2024
                </p>
              </div>
            </div>
          </div>

          {/* CTA Button */}
          <div className="pt-4">
            <button className="bg-primary-blue text-button-text font-satoshi font-bold px-5 py-3.5 rounded-[10px] hover:bg-blue-700 transition-colors w-full text-[16px] shadow-md">
              Book Safety Call
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CyberInsuranceCard;
