import React from 'react';
import HealthInsuranceCard from './HealthInsuranceCard';
import LifeInsuranceCard from './LifeInsuranceCard';
import CyberInsuranceCard from './CyberInsuranceCard';
import HomeInsuranceCard from './HomeInsuranceCard';
import MotorInsuranceCard from './MotorInsuranceCard';
import TravelInsuranceCard from './TravelInsuranceCard';

const Hero: React.FC = () => {
  return (
    <section className="pt-4 pb-16">
      <div className="flex">
        {/* Left Content - Exact positioning from Figma */}
        <div className="w-[408px] space-y-8">
          {/* Main Headline */}
          <h1 className="font-clash-display font-semibold text-hero text-design-black leading-[0.93em]">
            Insurance<br />
            Minus the<br />
            Drama
          </h1>

          {/* Subtitle */}
          <p className="font-satoshi font-normal text-subheading text-design-black leading-[1.16em] max-w-[408px]">
            No drama, just coverage that works when life doesn't.
          </p>

          {/* Description */}
          <p className="font-satoshi font-normal text-body text-design-black leading-[1.16em] max-w-[408px]">
            IRDAI-approved coverage. Built in Bangalore, trusted nationwide since 2018
          </p>

          {/* CTA Button */}
          <button className="bg-primary-blue text-button-text font-satoshi font-bold px-5 py-3.5 rounded-[10px] hover:bg-blue-700 transition-colors">
            Book Safety Call
          </button>
        </div>

        {/* Right Product Grid - Moved further right and slightly bigger */}
        <div className="ml-[280px] grid grid-cols-2 gap-x-4 gap-y-4">
          {/* Health Insurance Card - Using the new component */}
          <div className="relative">
            <HealthInsuranceCard />
          </div>

          {/* Life Insurance Card - Using the new component */}
          <div className="relative">
            <LifeInsuranceCard />
          </div>

          {/* Travel Insurance Card - Using the new component */}
          <div className="relative">
            <TravelInsuranceCard />
          </div>

          {/* Motor Insurance Card - Using the new component */}
          <div className="relative">
            <MotorInsuranceCard />
          </div>

          {/* Home Insurance Card - Using the new component */}
          <div className="relative">
            <HomeInsuranceCard />
          </div>

          {/* Cyber Insurance Card - Using the new component */}
          <div className="relative">
            <CyberInsuranceCard />
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
