import React, { useState } from 'react';
import HealthInsuranceCard from './HealthInsuranceCard';
import LifeInsuranceCard from './LifeInsuranceCard';
import CyberInsuranceCard from './CyberInsuranceCard';
import HomeInsuranceCard from './HomeInsuranceCard';
import MotorInsuranceCard from './MotorInsuranceCard';
import TravelInsuranceCard from './TravelInsuranceCard';

const Hero: React.FC = () => {
  const [showForm, setShowForm] = useState(false);
  const [activeCard, setActiveCard] = useState<'health' | 'life' | 'motor' | null>(null);

  const handleBookSafetyCall = (cardType: 'health' | 'life' | 'motor') => {
    setActiveCard(cardType);
    setShowForm(true);
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setActiveCard(null);
  };

  // Function to render form fields based on card type
  const renderFormFields = () => {
    switch (activeCard) {
      case 'health':
      case 'life':
        return (
          <>
            {/* Phone No. */}
            <div>
              <label className="block font-satoshi font-normal text-[15px] text-design-black mb-2">
                Phone No.:
              </label>
              <input 
                type="tel" 
                className="w-full px-4 py-3 bg-white rounded-[8px] border border-gray-300 focus:outline-none focus:border-primary-blue text-[15px]"
                placeholder="Enter phone number"
              />
            </div>

            {/* Age */}
            <div>
              <label className="block font-satoshi font-normal text-[15px] text-design-black mb-2">
                Age:
              </label>
              <input 
                type="number" 
                className="w-full px-4 py-3 bg-white rounded-[8px] border border-gray-300 focus:outline-none focus:border-primary-blue text-[15px]"
                placeholder="Enter age"
              />
            </div>

            {/* Dependents */}
            <div>
              <label className="block font-satoshi font-normal text-[15px] text-design-black mb-2">
                Dependents:
              </label>
              <input 
                type="number" 
                className="w-full px-4 py-3 bg-white rounded-[8px] border border-gray-300 focus:outline-none focus:border-primary-blue text-[15px]"
                placeholder="Number of dependents"
              />
            </div>

            {/* Target sum insured */}
            <div>
              <label className="block font-satoshi font-normal text-[15px] text-design-black mb-2">
                Target sum insured:
              </label>
              <input 
                type="text" 
                className="w-full px-4 py-3 bg-white rounded-[8px] border border-gray-300 focus:outline-none focus:border-primary-blue text-[15px]"
                placeholder="Enter target amount"
              />
            </div>
          </>
        );
      
      case 'motor':
        return (
          <>
            {/* Phone No. */}
            <div>
              <label className="block font-satoshi font-normal text-[15px] text-design-black mb-2">
                Phone No.:
              </label>
              <input 
                type="tel" 
                className="w-full px-4 py-3 bg-white rounded-[8px] border border-gray-300 focus:outline-none focus:border-primary-blue text-[15px]"
                placeholder="Enter phone number"
              />
            </div>

            {/* Registration No */}
            <div>
              <label className="block font-satoshi font-normal text-[15px] text-design-black mb-2">
                Registration No:
              </label>
              <input 
                type="text" 
                className="w-full px-4 py-3 bg-white rounded-[8px] border border-gray-300 focus:outline-none focus:border-primary-blue text-[15px]"
                placeholder="Enter registration number"
              />
            </div>

            {/* Policy expiry */}
            <div>
              <label className="block font-satoshi font-normal text-[15px] text-design-black mb-2">
                Policy expiry:
              </label>
              <input 
                type="date" 
                className="w-full px-4 py-3 bg-white rounded-[8px] border border-gray-300 focus:outline-none focus:border-primary-blue text-[15px]"
                placeholder="Select expiry date"
              />
            </div>

            {/* Make */}
            <div>
              <label className="block font-satoshi font-normal text-[15px] text-design-black mb-2">
                Make:
              </label>
              <input 
                type="text" 
                className="w-full px-4 py-3 bg-white rounded-[8px] border border-gray-300 focus:outline-none focus:border-primary-blue text-[15px]"
                placeholder="Enter vehicle make"
              />
            </div>

            {/* Model */}
            <div>
              <label className="block font-satoshi font-normal text-[15px] text-design-black mb-2">
                Model:
              </label>
              <input 
                type="text" 
                className="w-full px-4 py-3 bg-white rounded-[8px] border border-gray-300 focus:outline-none focus:border-primary-blue text-[15px]"
                placeholder="Enter vehicle model"
              />
            </div>
          </>
        );
      
      default:
        return null;
    }
  };

  return (
    <section className="pt-4 pb-16">
      <div className="flex">
        {/* Left Content - Exact positioning from Figma */}
        <div className="w-[408px] space-y-8">
          {/* Main Headline */}
          <h1 className="font-clash-display font-semibold text-hero text-design-black leading-[0.93em]">
            Insurance<br />
            Minus the<br />
            Drama
          </h1>

          {/* Subtitle */}
          <p className="font-satoshi font-normal text-subheading text-design-black leading-[1.16em] max-w-[408px]">
            No drama, just coverage that works when life doesn't.
          </p>

          {/* Description */}
          <p className="font-satoshi font-normal text-body text-design-black leading-[1.16em] max-w-[408px]">
            IRDAI-approved coverage. Built in Bangalore, trusted nationwide since 2018
          </p>

          {/* CTA Button */}
          <button className="bg-primary-blue text-button-text font-satoshi font-bold px-5 py-3.5 rounded-[10px] hover:bg-blue-700 transition-colors">
            Book Safety Call
          </button>
        </div>

        {/* Right Product Grid - Moved further right and slightly bigger */}
        <div className="ml-[280px] relative">
          <div className="grid grid-cols-2 gap-x-4 gap-y-4">
            {/* Health Insurance Card - Using the new component */}
            <div className="relative">
              <HealthInsuranceCard onBookSafetyCall={() => handleBookSafetyCall('health')} />
            </div>

            {/* Life Insurance Card - Using the new component */}
            <div className="relative">
              <LifeInsuranceCard onBookSafetyCall={() => handleBookSafetyCall('life')} />
            </div>

            {/* Travel Insurance Card - Using the new component */}
            <div className="relative">
              <TravelInsuranceCard />
            </div>

            {/* Motor Insurance Card - Using the new component */}
            <div className="relative">
              <MotorInsuranceCard onBookSafetyCall={() => handleBookSafetyCall('motor')} />
            </div>

            {/* Home Insurance Card - Using the new component */}
            <div className="relative">
              <HomeInsuranceCard />
            </div>

            {/* Cyber Insurance Card - Using the new component */}
            <div className="relative">
              <CyberInsuranceCard />
            </div>
          </div>

          {/* Cards Section Form Overlay */}
          {showForm && (
            <div className="absolute inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center">
              <div className="bg-light-gray p-6 w-full h-full shadow-2xl">
                {/* Form Header */}
                <div className="flex justify-between items-start mb-4">
                  <h3 className="font-satoshi font-normal text-[28px] text-design-black leading-[1.16em]">
                    {activeCard === 'health' ? 'Health' : activeCard === 'life' ? 'Life' : 'Motor'}
                  </h3>
                </div>

                {/* Form Content */}
                <div className="space-y-4">
                  {/* Form Fields */}
                  <div className="space-y-4">
                    {renderFormFields()}
                  </div>

                  {/* Form Buttons */}
                  <div className="pt-6 flex gap-3">
                    <button 
                      onClick={handleCloseForm}
                      className="flex-1 text-design-black font-satoshi font-bold px-4 py-3 rounded-[10px] border-2 border-design-black hover:bg-gray-100 transition-colors text-[15px]"
                    >
                      Cancel
                    </button>
                    <button 
                      className="flex-1 bg-primary-blue text-button-text font-satoshi font-bold px-4 py-3 rounded-[10px] hover:bg-blue-700 transition-colors text-[15px] shadow-md"
                    >
                      Schedule Safety Call
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </section>
  );
};

export default Hero;
