import React from 'react';

interface ValueCardProps {
  title: string;
  subtitle: string;
  founderName: string;
  quote: string;
  avatarPosition: 'left' | 'center' | 'right';
}

const ValueCard: React.FC<ValueCardProps> = ({ title, subtitle, founderName, quote, avatarPosition }) => {
  return (
    <div className="w-[315px] h-[293px] bg-light-gray rounded-[38px] p-8 flex flex-col justify-between">
      <div className="space-y-4">
        <h3 className="font-satoshi font-normal text-values text-design-black leading-[1.16em]">
          {title}
        </h3>
        <p className="font-satoshi font-normal text-values text-design-black leading-[1.16em]">
          {subtitle}
        </p>
      </div>
      
      <div className="flex items-center gap-4">
        <div className="w-12 h-12 bg-light-gray border-2 border-design-black rounded-full"></div>
        <div>
          <p className="font-satoshi font-normal text-founder text-design-black leading-[1.16em]">
            {founderName}
          </p>
        </div>
      </div>
    </div>
  );
};

const CoreValues: React.FC = () => {
  const values = [
    {
      title: "Demystifying",
      subtitle: "Complexity, Simplified",
      founderName: "-Sandeep Kumar",
      quote: "Paying insurance isn't the problem, it's the solution.",
      avatarPosition: 'left' as const
    },
    {
      title: "Need-based approach",
      subtitle: "",
      founderName: "-Koustav Bhattacharjee",
      quote: "Paying insurance isn't the problem, it's the solution.",
      avatarPosition: 'center' as const
    }
  ];

  return (
    <section className="px-10 py-16">
      <div className="mb-8">
        <h2 className="font-satoshi font-normal text-[32px] text-design-black leading-[1.16em]">
          Core Values
        </h2>
      </div>
      
      <div className="flex gap-8 mb-16">
        {values.map((value, index) => (
          <ValueCard
            key={index}
            title={value.title}
            subtitle={value.subtitle}
            founderName={value.founderName}
            quote={value.quote}
            avatarPosition={value.avatarPosition}
          />
        ))}
      </div>

      {/* Founder Notes Section */}
      <div className="mb-8">
        <h2 className="font-satoshi font-normal text-[32px] text-design-black leading-[1.16em]">
          Founders Notes
        </h2>
      </div>

      <div className="grid grid-cols-2 gap-8">
        <div className="w-[489px] h-[275px] bg-light-gray rounded-[16px] p-8">
          <p className="font-satoshi font-normal text-[32px] text-design-black leading-[1.16em]">
            Paying insurance isn't the problem, it's the solution.
          </p>
        </div>
        <div className="w-[489px] h-[275px] bg-light-gray rounded-[16px] p-8">
          <p className="font-satoshi font-normal text-[32px] text-design-black leading-[1.16em]">
            Paying insurance isn't the problem, it's the solution.
          </p>
        </div>
      </div>

      {/* CTA Button */}
      <div className="mt-8">
        <button className="bg-primary-blue text-button-text font-satoshi font-bold px-6 py-4 rounded-[12px] hover:bg-blue-700 transition-colors">
          Book Safety Call
        </button>
      </div>
    </section>
  );
};

export default CoreValues;
