import React, { useState } from 'react';
import apiService from '../services/api';
import emailService from '../services/emailService';

const AboutUs: React.FC = () => {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    phone: ''
  });
  const [submitting, setSubmitting] = useState(false);

  const handleBookSafetyCall = () => {
    // Close any open card forms by dispatching a custom event
    window.dispatchEvent(new CustomEvent('closeCardForms'));
    setShowForm(true);
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setFormData({ name: '', phone: '' });
  };

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleFormSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      // Submit form data to API
      await apiService.createSafetyCall({
        product_id: null,
        customer_name: formData.name,
        phone_number: formData.phone,
        email: '',
        form_data: formData
      });

      // Send email notification
      await emailService.sendFormspreeNotification({
        product_id: null,
        customer_name: formData.name,
        phone_number: formData.phone,
        email: '',
        form_data: formData
      });

      // Close form and reset
      setShowForm(false);
      setFormData({ name: '', phone: '' });
    } catch (error) {
      console.error('Error submitting form:', error);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <section className="pt-8 pb-16">
      {/* About Us Section */}
      <div className="flex justify-between items-start mb-8">
        {/* Left side: About Us heading */}
        <div className="flex-shrink-0">
          <h2 className="font-clash-display font-semibold text-section-heading text-design-black leading-[0.93em]">
            About Us
          </h2>
        </div>

        {/* Middle: Description text */}
        <div className="max-w-[440px] mx-8">
          <p className="font-satoshi font-normal text-[28px] text-design-black leading-[1.16em]">
            Nicsan Insurance is an Organically grown business by two simple people from simple beginnings.
          </p>
        </div>

        {/* Right side: Logo */}
        <div className="flex-shrink-0">
          <img 
            src="/images/logo-horizontal.png" 
            alt="Nicsan Insurance" 
            className="w-[235px] h-[43px]"
          />
        </div>
      </div>

      {/* Core Values Section */}
      <div className="mb-4">
        <h2 className="font-satoshi font-normal text-[32px] text-design-black leading-[1.16em]">
          Core Values
        </h2>
      </div>

      <div className="flex gap-8 mb-8">
        {/* Card 1: Demystifying */}
        <div className="w-[280px] h-[180px] bg-light-gray rounded-[20px] p-8 flex flex-col justify-between relative">
          <h3 className="font-satoshi font-normal text-[24px] text-design-black leading-[1.16em]">
            Demystifying
          </h3>
          {/* Icon in bottom right */}
          <div className="absolute bottom-6 right-6">
            <img 
              src="/images/honest.png" 
              alt="Demystifying Icon" 
              className="w-12 h-12 object-contain"
            />
          </div>
        </div>

        {/* Card 2: Complexity, Simplified */}
        <div className="w-[280px] h-[180px] bg-light-gray rounded-[20px] p-8 flex flex-col justify-between relative">
          <h3 className="font-satoshi font-normal text-[24px] text-design-black leading-[1.16em]">
            Complexity, Simplified
          </h3>
          {/* Icon in bottom right */}
          <div className="absolute bottom-6 right-6">
            <img 
              src="/images/simplify.png" 
              alt="Complexity Simplified Icon" 
              className="w-12 h-12 object-contain"
            />
          </div>
        </div>

        {/* Card 3: Need based approach */}
        <div className="w-[280px] h-[180px] bg-light-gray rounded-[20px] p-8 flex flex-col justify-between relative">
          <h3 className="font-satoshi font-normal text-[24px] text-design-black leading-[1.16em]">
            Need based approach
          </h3>
          {/* Icon in bottom right */}
          <div className="absolute bottom-6 right-6">
            <img 
              src="/images/target-audience.png" 
              alt="Need Based Approach Icon" 
              className="w-12 h-12 object-contain"
            />
          </div>
        </div>
      </div>

      {/* Founders Notes Section */}
      <div className="mb-4">
        <h2 className="font-satoshi font-normal text-[32px] text-design-black leading-[1.16em]">
          Founders Notes
        </h2>
      </div>

      <div className="flex gap-8 items-start">
        <div className="flex gap-8">
          {/* Founder Card 1: Sandeep Kumar */}
          <div className="w-[440px] h-[220px] bg-light-gray rounded-[20px] p-8 flex flex-col justify-between">
            <p className="font-satoshi font-normal text-[24px] text-design-black leading-[1.16em]">
              Paying insurance isn't the problem, it's the solution.
            </p>
            <p className="font-satoshi font-normal text-[18px] text-design-black leading-[1.16em]">
              -Sandeep Kumar
            </p>
          </div>

          {/* Founder Card 2: Koustav Bhattacharjee */}
          <div className="w-[440px] h-[220px] bg-light-gray rounded-[20px] p-8 flex flex-col justify-between">
            <p className="font-satoshi font-normal text-[24px] text-design-black leading-[1.16em]">
            It is the coverages that matter at the time of claim, not the premium you have paid.
            </p>
            <p className="font-satoshi font-normal text-[18px] text-design-black leading-[1.16em]">
              -Koustav Bhattacharjee
            </p>
          </div>
        </div>

        {/* CTA Button */}
        <div className="flex-shrink-0">
          <button 
            onClick={handleBookSafetyCall}
            className="bg-primary-blue text-button-text font-satoshi font-bold px-6 py-4 rounded-[12px] hover:bg-[#012E58] transition-colors"
          >
            Book Safety Call
          </button>
        </div>
      </div>

      {/* Form Overlay */}
      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center">
          <div className="bg-light-gray p-6 w-full max-w-md rounded-[24px] overflow-hidden">
            {/* Form Header */}
            <div className="flex justify-between items-start mb-4">
              <h3 className="font-satoshi font-normal text-[28px] text-design-black leading-[1.16em]">
                Book Safety Call
              </h3>
              <button 
                onClick={handleCloseForm}
                className="text-design-black hover:text-gray-600"
              >
                ✕
              </button>
            </div>

            {/* Form Content */}
            <form onSubmit={handleFormSubmit} className="space-y-4">
              <div>
                <label className="block font-satoshi font-normal text-[15px] text-design-black mb-2">
                  Name:
                </label>
                <input 
                  type="text"
                  className="w-full px-4 py-3 bg-white rounded-[8px] border border-gray-300 focus:outline-none focus:border-primary-blue text-[15px]"
                  placeholder="Enter your full name"
                  value={formData.name}
                  onChange={(e) => handleInputChange('name', e.target.value)}
                  required
                />
              </div>

              <div>
                <label className="block font-satoshi font-normal text-[15px] text-design-black mb-2">
                  Phone No.:
                </label>
                <input 
                  type="tel"
                  className="w-full px-4 py-3 bg-white rounded-[8px] border border-gray-300 focus:outline-none focus:border-primary-blue text-[15px]"
                  placeholder="Enter your phone number"
                  value={formData.phone}
                  onChange={(e) => handleInputChange('phone', e.target.value)}
                  required
                />
              </div>

              {/* Form Buttons */}
              <div className="flex gap-3 pt-4">
                <button 
                  type="button"
                  onClick={handleCloseForm}
                  disabled={submitting}
                  className="flex-1 text-design-black font-satoshi font-bold px-4 py-3 rounded-[10px] border-2 border-design-black hover:bg-gray-100 transition-colors text-[15px] disabled:opacity-50"
                >
                  Cancel
                </button>
                <button 
                  type="submit"
                  disabled={submitting}
                  className="flex-1 bg-primary-blue text-button-text font-satoshi font-bold px-4 py-3 rounded-[10px] hover:bg-[#012E58] transition-colors text-[15px] shadow-md disabled:opacity-50"
                >
                  {submitting ? 'Submitting...' : 'Schedule Safety Call'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </section>
  );
};

export default AboutUs;
