import React from 'react';

const Hero: React.FC = () => {
  const products = [
    {
      title: "Health",
      description: "Stay healthy,\nstay wealthy",
      icon: "/images/healthcare-icon.png",
      iconAlt: "Healthcare Icon"
    },
    {
      title: "Life",
      description: "Protect\ntheir future",
      icon: "/images/life-insurance-icon.png",
      iconAlt: "Life Insurance Icon"
    },
    {
      title: "Travel",
      description: "Roam the globe,\nworry-free",
      icon: "/images/world-icon.png",
      iconAlt: "World Icon"
    },
    {
      title: "Motor",
      description: "Drive with peace\nof mind",
      icon: "/images/cyber-security-icon.png", // Using placeholder for motor
      iconAlt: "Motor Insurance Icon"
    },
    {
      title: "Home",
      description: "Make your castle\ndisaster-proof",
      icon: "/images/cyber-security-icon.png", // Using placeholder for home
      iconAlt: "Home Insurance Icon"
    },
    {
      title: "Cyber",
      description: "Protect your\ndigital life",
      icon: "/images/cyber-security-icon.png",
      iconAlt: "Cyber Security Icon"
    }
  ];

  return (
    <section className="pt-4 pb-16">
      <div className="flex">
        {/* Left Content - Exact positioning from Figma */}
        <div className="w-[408px] space-y-8">
          {/* Main Headline */}
          <h1 className="font-clash-display font-semibold text-hero text-design-black leading-[0.93em]">
            Insurance<br />
            Minus the<br />
            Drama
          </h1>

          {/* Subtitle */}
          <p className="font-satoshi font-normal text-subheading text-design-black leading-[1.16em] max-w-[408px]">
            No drama, just coverage that works when life doesn't.
          </p>

          {/* Description */}
          <p className="font-satoshi font-normal text-body text-design-black leading-[1.16em] max-w-[408px]">
            IRDAI-approved coverage. Built in Bangalore, trusted nationwide since 2018
          </p>

          {/* CTA Button */}
          <button className="bg-primary-blue text-button-text font-satoshi font-bold px-5 py-3.5 rounded-[10px] hover:bg-blue-700 transition-colors">
            Book Safety Call
          </button>
        </div>

        {/* Right Product Grid - Moved further right and slightly bigger */}
        <div className="ml-[280px] grid grid-cols-2 gap-x-4 gap-y-4">
          {products.map((product, index) => (
            <div 
              key={index} 
              className={`w-[300px] bg-light-gray rounded-[34px] p-6 flex flex-col justify-between transition-all duration-300 ${
                index === 1 ? 'h-[170px] hover:h-[280px] hover:shadow-xl hover:bg-gray-200 cursor-pointer hover:border-2 hover:border-primary-blue hover:z-20 hover:absolute hover:top-0 hover:left-0' : 'h-[170px]'
              }`}
            >
              <div className="flex justify-between items-start">
                <div className="space-y-3">
                  <h3 className="font-satoshi font-normal text-[30px] text-design-black leading-[1.16em]">
                    {product.title}
                  </h3>
                  <p className="font-satoshi font-light text-[17px] text-design-black leading-[1.16em] max-w-[150px]">
                    {product.description}
                  </p>
                </div>
                <img 
                  src={product.icon} 
                  alt={product.iconAlt} 
                  className="w-[48px] h-[48px] object-contain"
                />
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Hero;
