import React from 'react';

interface ProductCardProps {
  title: string;
  description: string;
  icon: string;
  iconAlt: string;
}

const ProductCard: React.FC<ProductCardProps> = ({ title, description, icon, iconAlt }) => {
  return (
    <div className="w-[408px] h-[256px] bg-light-gray rounded-[34px] p-8 flex flex-col justify-between">
      <div className="flex justify-between items-start">
        <div className="space-y-4">
          <h3 className="font-satoshi font-normal text-product-title text-design-black leading-[1.16em]">
            {title}
          </h3>
          <p className="font-satoshi font-light text-[25px] text-design-black leading-[1.16em] max-w-[183px]">
            {description}
          </p>
        </div>
        <img 
          src={icon} 
          alt={iconAlt} 
          className="w-[70px] h-[70px] object-contain"
        />
      </div>
    </div>
  );
};

const ProductGrid: React.FC = () => {
  const products = [
    {
      title: "Health",
      description: "Stay healthy,\nstay wealthy",
      icon: "/images/healthcare-icon.png",
      iconAlt: "Healthcare Icon"
    },
    {
      title: "Life",
      description: "Protect\ntheir future",
      icon: "/images/life-insurance-icon.png",
      iconAlt: "Life Insurance Icon"
    },
    {
      title: "Travel",
      description: "Roam the globe,\nworry-free",
      icon: "/images/world-icon.png",
      iconAlt: "World Icon"
    },
    {
      title: "Motor",
      description: "Drive with peace\nof mind",
      icon: "/images/cyber-security-icon.png", // Using placeholder for motor
      iconAlt: "Motor Insurance Icon"
    },
    {
      title: "Home",
      description: "Make your castle\ndisaster-proof",
      icon: "/images/cyber-security-icon.png", // Using placeholder for home
      iconAlt: "Home Insurance Icon"
    },
    {
      title: "Cyber",
      description: "Protect your\ndigital life",
      icon: "/images/cyber-security-icon.png",
      iconAlt: "Cyber Security Icon"
    }
  ];

  return (
    <section className="px-10 py-16">
      <div className="grid grid-cols-3 gap-x-[66px] gap-y-[156px]">
        {products.map((product, index) => (
          <ProductCard
            key={index}
            title={product.title}
            description={product.description}
            icon={product.icon}
            iconAlt={product.iconAlt}
          />
        ))}
      </div>
    </section>
  );
};

export default ProductGrid;
